#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\promt_optimizer\node_modules\.pnpm\electron-builder@24.13.3_el_2d94b8d0e843602ee4381a4d0eb844a7\node_modules\electron-builder\node_modules;D:\promt_optimizer\node_modules\.pnpm\electron-builder@24.13.3_el_2d94b8d0e843602ee4381a4d0eb844a7\node_modules;D:\promt_optimizer\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/promt_optimizer/node_modules/.pnpm/electron-builder@24.13.3_el_2d94b8d0e843602ee4381a4d0eb844a7/node_modules/electron-builder/node_modules:/mnt/d/promt_optimizer/node_modules/.pnpm/electron-builder@24.13.3_el_2d94b8d0e843602ee4381a4d0eb844a7/node_modules:/mnt/d/promt_optimizer/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../electron-builder/install-app-deps.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../electron-builder/install-app-deps.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../electron-builder/install-app-deps.js" $args
  } else {
    & "node$exe"  "$basedir/../electron-builder/install-app-deps.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
